mapper.delete:

DELETE 
FROM jet_class_pick
WHERE jet_class_detail_no = #{jet_class_detail_no}
AND jet_member_no = #{jet_member_no}

mapper.select:

SELECT *
FROM jet_class_pick
WHERE jet_class_detail_no = #{jet_class_detail_no} 
AND jet_member_no = #{jet_member_no}


SELECT count(*)
FROM jet_board_class_singo 
WHERE jet_board_class_detail_no = #{jet_board_class_detail_no}
AND jet_member_no = #{jet_member_no}
